import { Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChanges } from '@angular/core';
import { Route, Router } from '@angular/router';
import { count } from 'rxjs';
import { ICategory } from 'src/app/Models/icategory';
import { Iproduct } from 'src/app/Models/iproduct';
import { StaticDataService } from 'src/app/Services/static-data.service';
@Component({
  selector: 'app-order',
  templateUrl: './order.component.html',
  styleUrls: ['./order.component.css']
})
export class OrderComponent implements OnChanges , OnInit{


  // PrdList:Iproduct[];
  filteredPrdList:Iproduct[]=[];
  // CatList:ICategory[]
  TotalPrice:number=0;
  // SelectedID:number=0;
  @Input() SendCatId:number=0;
  @Output() NewTotalPrice:EventEmitter<number>; //Generic

  constructor(private router:Router,private Static:StaticDataService )
  {
    this.NewTotalPrice=new EventEmitter<number>();
//     this.PrdList=[
//   {
//     id: 1,
//     name: "Laptop",
//     price: 1500,
//     imgUrl: "assets/img1.jpg",
//     quantity: 1,
//     categoryId: 1
//   },
//   {
//     id: 2,
//     name: "Smartphone",
//     price: 800,
//     imgUrl: "assets/img4.jpg",
//     quantity: 20,
//     categoryId: 1
//   },
//   {
//     id: 3,
//     name: "Headphones",
//     price: 150,
//     imgUrl: "assets/img2.jpg",
//     quantity: 30,
//     categoryId: 2
//   },
//   {
//     id: 4,
//     name: "Keyboard",
//     price: 100,
//     imgUrl: "assets/img3.jpg",
//     quantity: 1,
//     categoryId: 2
//   },
//   {
//     id: 5,
//     name: "Monitor",
//     price: 300,
//     imgUrl: "assets/img4.jpg",
//     quantity: 8,
//     categoryId: 3
//   },
//     {
//     id: 6,
//     name: "Monitor",
//     price: 3020,
//     imgUrl: "assets/img1.jpg",
//     quantity: 0,
//     categoryId: 2
//   }
// ];

// this.filteredPrdList=this.PrdList;

// this.CatList=[
//   {
//     id:1,
//     name:"food"
//   },{
//         id:2,
//     name:"others"
//   }
// ];
  }
  ngOnInit(): void {
    this.getAll()
  }


  // enhancement Product
  PrdTrackBy(index:number,prd:Iproduct){
    return prd.id
  }

  Buy(PrdPrice:number,Count:any){
    // console.log(PrdPrice);
    // console.log(count);
    this.TotalPrice+=Number(Count) * PrdPrice;
    this.NewTotalPrice.emit(this.TotalPrice);
  }

  // ChangeID(){
  //   this.SelectedID=2;
  // }

  FilteratedPrds(){
    this.filteredPrdList=this.Static.getPrdByCID(this.SendCatId);
    // if(this.SendCatId==0){
    //   this.filteredPrdList=this.PrdList;
    // }else{
    //   this.filteredPrdList=this.PrdList.filter(i=>i.categoryId==this.SendCatId);
    // }

  }

  // Related to @Input()-------------------->Component LifCycle
    ngOnChanges(): void {
    this.FilteratedPrds();
  }



  getAll(){
    this.filteredPrdList=this.Static.getAllPrds();
  }

  OpenPrdDtail(PID:number){
    this.router.navigateByUrl('/order/'+PID);
  }

  DeleteById(PID:number){
    this.Static.deletePrdById(PID);
  }

}

<!-- <select 
  class="form-select" 
  aria-label="Default select example" 
  [(ngModel)]="SelectedID" 
  (ngModelChange)="SelectedID = +$event"    (change)="FilteratedPrds()">   ✅ مهم لتحويل القيمة إلى رقم -->
  <!-- <option [value]="cat.id" *ngFor="let cat of CatList">
    {{cat.name}}
  </option>
</select> -->

<!-- <h1>{{ SelectedID }}</h1>

<button class="btn btn-dark" (click)="ChangeID()">Change</button> -->

<table class="table">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Name</th>
      <th scope="col">Imag</th>
      <th scope="col">Quantity</th>
      <th scope="col">Price</th>   
         <th scope="col"  style="text-align: center;">Count</th> 
         <th></th>
         <th>More</th>
         <th></th>
    </tr>
  </thead>
  <tbody>
    <!-- <tr *ngFor="let p of PrdList ; let i=index"> -->
    <!-- <tr *ngFor="let p of PrdList ; index as i ; trackBy:PrdTrackBy" [hidden]="p.quantity===0">     -->
     <tr *ngFor="let p of filteredPrdList ; index as i ; trackBy:PrdTrackBy" >    
        <td>{{i+1}}</td>
      <td>{{p.name|uppercase}}</td>
      <!-- Properity binding -->
      <td><img [src]="p.imgUrl" alt="" width="100" height="100" appLightbox> </td>
      <!-- <td>{{p.quantity}}</td> -->

       <!-- template variable -->
        <!-- <td *ngIf="p.quantity!==0;else OutOfStock">{{p.quantity}}</td>
        <ng-template #OutOfStock>Out OF Stock</ng-template> -->

       <!-- Switch  -->

         <div [ngSwitch]="p.quantity" [ngClass]="{'bg-danger':p.quantity===0,'bg-warning':p.quantity===1}" [ngStyle]="{color:p.quantity===0?'white':'black'}">
           <td *ngSwitchCase="0">Out Of Stock</td>
           <td *ngSwitchCase="1">Last Item</td>
           <td *ngSwitchDefault>{{p.quantity}}</td>
         </div>

      <td >{{p.price}}</td>
      <td> <input type="number" class="form-control" #itemInp></td>
      <td><button class="btn btn-dark btn-sm" (click)="Buy(p.price, itemInp.value)" >Buy</button></td>
      <!-- <td><a [routerLink]="['/order',p.id]" class="btn btn-dark btn-sm">See More</a></td>-->
       <td><button class="btn btn-dark btn-sm" (click)="OpenPrdDtail(p.id)">See More</button></td>
          <td><button class="btn btn-dark btn-sm" (click)="DeleteById(p.id)">Delete</button></td>

      </tr>

  </tbody>
</table>
