import { Directive, ElementRef } from '@angular/core';

@Directive({
  selector: '[appLightbox]'
})
export class LightboxDirective {

  constructor(private elem:ElementRef) { 
    this.elem.nativeElement.style.border="5px solid green"
  }

}
