import { Component } from '@angular/core';
import { ICategory } from 'src/app/Models/icategory';

@Component({
  selector: 'app-master-order',
  templateUrl: './master-order.component.html',
  styleUrls: ['./master-order.component.css']
})
export class MasterOrderComponent {
   SelectedID: number = 0;
      CatList:ICategory[];

      constructor()
  {

this.CatList=[
  {
    id:1,
    name:"food"
  },{
        id:3,
    name:"others"
  }
];
  }



}
<select 
  class="form-select" 
  aria-label="Default select example" 
  [(ngModel)]="SelectedID" 
  (ngModelChange)="SelectedID = +$event" >   <!-- ✅ مهم لتحويل القيمة إلى رقم -->
  <option [value]="cat.id" *ngFor="let cat of CatList">
    {{cat.name}}
  </option>
</select>
<h1>{{SelectedID}}</h1>
<app-order [SendCatId]="SelectedID"></app-order>
