import { Component } from '@angular/core';
import { ICategory } from 'src/app/Models/icategory';
import { Iproduct } from 'src/app/Models/iproduct';
@Component({
  selector: 'app-order',
  templateUrl: './order.component.html',
  styleUrls: ['./order.component.css']
})
export class OrderComponent {

  PrdList:Iproduct[];
  CatList:ICategory[]

  constructor()
  {
    this.PrdList=[
  {
    id: 1,
    name: "Laptop",
    price: 1500,
    imgUrl: "assets/img1.jpg",
    quantity: 1,
    categoryId: 1
  },
  {
    id: 2,
    name: "Smartphone",
    price: 800,
    imgUrl: "assets/img4.jpg",
    quantity: 20,
    categoryId: 1
  },
  {
    id: 3,
    name: "Headphones",
    price: 150,
    imgUrl: "assets/img2.jpg",
    quantity: 30,
    categoryId: 2
  },
  {
    id: 4,
    name: "Keyboard",
    price: 100,
    imgUrl: "assets/img3.jpg",
    quantity: 1,
    categoryId: 2
  },
  {
    id: 5,
    name: "Monitor",
    price: 300,
    imgUrl: "assets/img4.jpg",
    quantity: 8,
    categoryId: 3
  },
    {
    id: 6,
    name: "Monitor",
    price: 3020,
    imgUrl: "assets/img1.jpg",
    quantity: 0,
    categoryId: 2
  }
];

this.CatList=[
  {
    id:1,
    name:"food"
  },{
        id:1,
    name:"others"
  }
];
  }

  // enhancement Product
  PrdTrackBy(index:number,prd:Iproduct){
    return prd.id
  }

}
