import { Component } from '@angular/core';
import { ICategory } from 'src/app/Models/icategory';

@Component({
  selector: 'app-master-order',
  templateUrl: './master-order.component.html',
  styleUrls: ['./master-order.component.css']
})
export class MasterOrderComponent {
   SelectedID: number = 0;
      CatList:ICategory[];
      total:number=0;
      constructor()
  {
//for order
// this.CatList=[
//   {
//     id:1,
//     name:"food"
//   },{
//         id:2,
//     name:"others"
//   }
// ];
//for product
this.CatList=[
  {
    id: 1,
    name: "Electronics"
  },
  {
    id: 2,
    name:"Accessories"
  },
  {
    id: 3,
    name:"Wearables"
  }
];
  }

OnTotalPrice(Price:number){
  this.total=Price;
}

}
<div>
<select 
  class="form-select" 
  aria-label="Default select example" 
  [(ngModel)]="SelectedID" 
  (ngModelChange)="SelectedID = +$event" >   <!-- ✅ مهم لتحويل القيمة إلى رقم -->
  <option [value]="cat.id" *ngFor="let cat of CatList">
    {{cat.name}}
  </option>
</select>
</div>

<!-- <h1>{{SelectedID}}</h1> -->

<!-- <app-order [SendCatId]="SelectedID" (NewTotalPrice)="OnTotalPrice($event)"></app-order> -->
<div>
<app-product [SendCatId]="SelectedID" (NewTotalPrice)="OnTotalPrice($event)"></app-product>
</div>
  <div >
    <h2>Total Price: {{ total }}</h2>
  </div>
