import { Component } from '@angular/core';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
export class MainComponent {

  images = [
    { src: 'assets/img1.jpg', desc: 'Beautiful Landscape' },
    { src: 'assets/img2.jpg', desc: 'Sunny Beach' },
    { src: 'assets/img3.jpg', desc: 'Snowy Mountains' },
    { src: 'assets/img4.jpg', desc: 'City Lights' }
  ];

  currentIndex = 0;
  interval: any;
  showDesc = false;

  nextImage() {
    this.currentIndex = (this.currentIndex + 1) % this.images.length;
  }

  prevImage() {
    this.currentIndex = (this.currentIndex - 1 + this.images.length) % this.images.length;
  }

  startSlider() {
    if (!this.interval) {
      this.interval = setInterval(() => this.nextImage(), 500);
    }
  }

  stopSlider() {
    clearInterval(this.interval);
    this.interval = null;
  }

  toggleDescription() {
    this.showDesc = !this.showDesc;
  }

}
<div class="home-container">
  <img [src]="images[currentIndex].src" class="main-img " height="200px" alt="slider image" />

<div class="buttons">
  <div class="button-with-icon">
    <button (click)="prevImage()">Previous</button>
    <div class="icon" (click)="toggleDescription()">
      <i class="fa fa-info-circle"></i>
    </div>
  </div>

  <button (click)="nextImage()">Next</button>
  <button (click)="startSlider()">Start Slider</button>
  <button (click)="stopSlider()">Stop</button>
</div>

<p *ngIf="showDesc">{{ images[currentIndex].desc }}</p>
